package slidingwindow.allanagrams;

import java.util.*;

public class Solution {

    public static void main(String[] args) {
        Solution sol = new Solution();
        sol.findAnagrams(
                "",
                "");
        // sol.findAnagrams("cbac", "abc");
    }

    public List<Integer> findAnagrams(String s, String p) {
        char[] sArray = s.toCharArray();
        char[] pArray = p.toCharArray();

        int sLen = sArray.length;
        int pLen = pArray.length;

        Map<String, Integer> freqMapForP = new HashMap<>();
        for (int i = 0; i < pLen; i++) {
            if (freqMapForP.containsKey(pArray[i] + "")) {
                freqMapForP.put(pArray[i] + "", freqMapForP.get(pArray[i] + "") + 1);
            } else {
                freqMapForP.put(pArray[i] + "", 1);
            }
        }

        Map<String, Integer> sWindowfreqMap = new HashMap<>();

        int leftIndex = 0;
        int rightIndex = leftIndex + pLen - 1;

        for (int i = leftIndex; i <= rightIndex; i++) {
            if (sWindowfreqMap.containsKey(sArray[i] + "")) {
                sWindowfreqMap.put(sArray[i] + "", sWindowfreqMap.get(sArray[i] + "") + 1);
            } else {
                sWindowfreqMap.put(sArray[i] + "", 1);
            }
        }

        List<Integer> result = new ArrayList<>();

        while ((rightIndex + 1) < sLen) {
            if (compareFreqMaps(sWindowfreqMap, freqMapForP)) {
                result.add(leftIndex);
            }

            // remove current elem
            if (sWindowfreqMap.containsKey(sArray[leftIndex] + "")) {
                sWindowfreqMap.put(sArray[leftIndex] + "", sWindowfreqMap.get(sArray[leftIndex] + "") - 1);
            }
            leftIndex++;

            // Add next elem
            rightIndex++;
            if (sWindowfreqMap.containsKey(sArray[rightIndex] + "")) {
                sWindowfreqMap.put(sArray[rightIndex] + "", sWindowfreqMap.get(sArray[rightIndex] + "") + 1);
            }
        }

        if (compareFreqMaps(sWindowfreqMap, freqMapForP)) {
            result.add(leftIndex);
        }

        return result;
    }

    private boolean compareFreqMaps (Map<String, Integer> sMap, Map<String, Integer> pMap) {
        Set<String> sKeySet = sMap.keySet();
        Set<String> pKeySet = pMap.keySet();

        if (sKeySet.size() != pKeySet.size()) {
            return false;
        }

        for (String key : pKeySet) {
            if (!sMap.containsKey(key) || sMap.get(key).intValue() != pMap.get(key).intValue()) {
                return false;
            }
        }

        return true;
    }
}
